sys_variable_value.value:
(function execute(inputs, outputs) {
    var status_code = inputs.status_code;
    if(/^20.$/.test(status_code)) {
       var body = inputs.body;
       var task = JSON.parse(body);
       outputs.task = task;
    } else {
       gs.error("Remote Contract Request task not submitted, HTTP Error status: " + status_code);
    }
})(inputs, outputs);

