sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Validate Contract Start and end Date

    var startDate = current.u_start_date;
    var endDate = current.u_end_date;

    var gdStartDate = new GlideDateTime(startDate);
    var gdEndDate = new GlideDateTime(endDate);

    var dateDifferenceInMs = Math.abs(gdEndDate.getNumericValue() - gdStartDate.getNumericValue());

    var dateDifferenceInDays = Math.floor(dateDifferenceInMs / (1000 * 60 * 60 * 24));

    var minDateDifferenceInDays = 60; // TODO: application property.

    if (dateDifferenceInDays < minDateDifferenceInDays) {
        current.startDate = "";
        current.endDate = "";
        current.setAbortAction(true);
        gs.addErrorMessage("Error: The minimum distance between the start and end date of the contract is 2 months, i.e. " + minDateDifferenceInDays + " days.");
    }

})(current, previous);
